import { useQuery, useMutation, useQueryClient } from 'react-query';
import { dogsApi, apiUtils } from '../services/api';
import { toast } from 'react-toastify';

// Query keys
export const QUERY_KEYS = {
  DOGS: 'dogs',
  DOG: 'dog',
};

// Hook to fetch all dogs with filters
export const useDogs = (filters = {}, options = {}) => {
  return useQuery(
    [QUERY_KEYS.DOGS, filters],
    () => dogsApi.getDogs(filters),
    {
      staleTime: 2 * 60 * 1000, // 2 minutes
      cacheTime: 5 * 60 * 1000, // 5 minutes
      onError: (error) => {
        const errorInfo = apiUtils.handleError(error);
        toast.error(`Failed to load dogs: ${errorInfo.message}`);
      },
      ...options,
    }
  );
};

// Hook to fetch single dog
export const useDog = (dogId, options = {}) => {
  return useQuery(
    [QUERY_KEYS.DOG, dogId],
    () => dogsApi.getDog(dogId),
    {
      enabled: !!dogId,
      staleTime: 5 * 60 * 1000, // 5 minutes
      cacheTime: 10 * 60 * 1000, // 10 minutes
      onError: (error) => {
        const errorInfo = apiUtils.handleError(error);
        toast.error(`Failed to load dog details: ${errorInfo.message}`);
      },
      ...options,
    }
  );
};

// Hook to vote on a dog
export const useVoteDog = () => {
  const queryClient = useQueryClient();

  return useMutation(
    ({ dogId, voteData }) => dogsApi.voteDog(dogId, voteData),
    {
      onSuccess: (data, variables) => {
        const { dogId } = variables;

        // Update the dog's vote counts in cache
        queryClient.setQueryData([QUERY_KEYS.DOG, dogId], (oldData) => {
          if (!oldData) return oldData;

          const updatedDog = { ...oldData.data };
          if (variables.voteData.vote_type === 'wag') {
            updatedDog.wag_count = (updatedDog.wag_count || 0) + 1;
          } else if (variables.voteData.vote_type === 'growl') {
            updatedDog.growl_count = (updatedDog.growl_count || 0) + 1;
          }

          return {
            ...oldData,
            data: updatedDog,
          };
        });

        // Show success message
        const voteType = variables.voteData.vote_type === 'wag' ? 'ðŸ• Wag' : 'ðŸ˜¤ Growl';
        toast.success(`${voteType} recorded successfully!`);
      },
      onError: (error) => {
        const errorInfo = apiUtils.handleError(error);
        toast.error(`Failed to record vote: ${errorInfo.message}`);
      },
    }
  );
};

// Custom hook for managing favorites (using localStorage)
export const useFavorites = () => {
  const getFavorites = () => {
    try {
      const favorites = localStorage.getItem('pupperFavorites');
      return favorites ? JSON.parse(favorites) : [];
    } catch (error) {
      console.error('Error reading favorites:', error);
      return [];
    }
  };

  const addToFavorites = (dogId) => {
    try {
      const favorites = getFavorites();
      if (!favorites.includes(dogId)) {
        const newFavorites = [...favorites, dogId];
        localStorage.setItem('pupperFavorites', JSON.stringify(newFavorites));
        toast.success('Added to favorites! â¤ï¸');
        return newFavorites;
      }
      return favorites;
    } catch (error) {
      console.error('Error adding to favorites:', error);
      toast.error('Failed to add to favorites');
      return getFavorites();
    }
  };

  const removeFromFavorites = (dogId) => {
    try {
      const favorites = getFavorites();
      const newFavorites = favorites.filter(id => id !== dogId);
      localStorage.setItem('pupperFavorites', JSON.stringify(newFavorites));
      toast.success('Removed from favorites');
      return newFavorites;
    } catch (error) {
      console.error('Error removing from favorites:', error);
      toast.error('Failed to remove from favorites');
      return getFavorites();
    }
  };

  const isFavorite = (dogId) => {
    const favorites = getFavorites();
    return favorites.includes(dogId);
  };

  const toggleFavorite = (dogId) => {
    if (isFavorite(dogId)) {
      return removeFromFavorites(dogId);
    } else {
      return addToFavorites(dogId);
    }
  };

  return {
    favorites: getFavorites(),
    addToFavorites,
    removeFromFavorites,
    isFavorite,
    toggleFavorite,
  };
};
