import React, { useState } from 'react';
import { dogsApi, apiUtils } from '../services/api';

const ApiTest = () => {
  const [loading, setLoading] = useState(false);
  const [result, setResult] = useState(null);
  const [error, setError] = useState(null);

  const testApi = async () => {
    setLoading(true);
    setError(null);
    setResult(null);

    try {
      console.log('Testing API connection...');
      const data = await dogsApi.getDogs();
      console.log('API test successful:', data);
      setResult(data);
    } catch (err) {
      console.error('API test failed:', err);
      const errorInfo = apiUtils.handleError(err);
      setError(errorInfo);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="p-6 bg-white rounded-lg shadow-sm border border-gray-200 mb-6">
      <h3 className="text-lg font-semibold mb-4">API Connection Test</h3>

      <button
        onClick={testApi}
        disabled={loading}
        className="btn-primary mb-4"
      >
        {loading ? 'Testing...' : 'Test API Connection'}
      </button>

      {loading && (
        <div className="text-blue-600">
          <div className="spinner w-6 h-6 inline-block mr-2"></div>
          Testing API connection...
        </div>
      )}

      {error && (
        <div className="bg-red-50 border border-red-200 rounded-lg p-4 mb-4">
          <h4 className="text-red-800 font-semibold mb-2">Error:</h4>
          <p className="text-red-700 mb-2">{error.message}</p>
          <p className="text-red-600 text-sm">Status: {error.status}</p>
          {error.data && (
            <pre className="text-xs text-red-600 mt-2 overflow-auto">
              {JSON.stringify(error.data, null, 2)}
            </pre>
          )}
        </div>
      )}

      {result && (
        <div className="bg-green-50 border border-green-200 rounded-lg p-4">
          <h4 className="text-green-800 font-semibold mb-2">Success!</h4>
          <p className="text-green-700 mb-2">
            API is working. Found {result.data?.count || 0} dogs.
          </p>
          <pre className="text-xs text-green-600 overflow-auto">
            {JSON.stringify(result, null, 2)}
          </pre>
        </div>
      )}
    </div>
  );
};

export default ApiTest;
